mapper.select:
select * from record where status in (0, 1)

    and vehicle_id = #{vehicleId}


    and driver_name = #{driverName}


    and freight_id = #{freightId}


    and record_id = #{recordId}

order by record_id desc
limit #{startNo},#{pageSize}

select count(1) from record where status in (0, 1)

    and vehicle_id = #{vehicleId}


    and driver_name = #{driverName}


    and freight_id = #{freightId}


    and record_id = #{recordId}

mapper.update:
update record set vehicle_id = #{vehicleId}, driver_id = #{driverId},
driver_name = #{driverName}, freight_id = #{freightId}, start_address = #{startAddress},
end_address = #{endAddress}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}
where record_id = #{recordId}
